import { Component, OnInit,ViewEncapsulation  } from '@angular/core';
import { DataService } from './data.service';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  //below allows components to overwight such as margin
  encapsulation: ViewEncapsulation.None,
  // styles
  styles:['*{ overflow:hidden;}']
})


export class AppComponent implements OnInit {
  notes: any;

  constructor(private dataService: DataService) { }

  ngOnInit():void {
    this.dataService.getData().subscribe(data => {
      this.notes = data; // Assign the fetched data to the component property
      console.log(data);
    });
  }

  showOverlay(){
    console.log("test works!");
  }
}


<div style="margin: 0 !important;padding: 0 !important; " >



<div class="container" style="display: block;border-width: 0px;margin: 0;padding-bottom: 30px;background-image: url(../assets/pictures/pexels-abdullah-ghatasheh-1631677\ \(1\).jpg);">

<div class="title" style="font-size: 20px; text-align: center;margin: 0;padding: 0; ">

<h1 style="margin-top: 50px;padding: 0; font-size: 65px; font-family: sans-serif;;">Notes</h1>
</div>

<div class="entire-container" style="margin-top: 40px;padding: 50px;padding-bottom: 300px;">
  <div class="button">
    <button (click)="showOverlay()"> button</button>
  </div>
  <ul>

    <ul class="note-container" style="display: flex; flex-wrap:wrap; justify-content:space-evenly; ">
      <li *ngFor="let note of notes" style="list-style: none; padding: 10px;">
        <div style="display: inline-block; background-color: aqua; font-size: 25px;  padding: 30px; border-radius: 10px; width: 300px;">
          <div class="note-name" style="padding-bottom: 10px; font-weight: bold;">
            {{note.name}}
          </div>
          <div class="note-category" style="padding-bottom: 10px; text-align: center;">
            {{note.category}}
          </div>
          {{note.description}}
        </div>
      </li>
    </ul>
  </ul>

</div>

</div>

</div>

<style>
  body {
    padding: 0;
    margin:0;
  }
</style>
