import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-overlay',
  templateUrl: './overlay.component.html',
  styleUrls: ['./overlay.component.css']
})
export class OverlayComponent {
  @Input() isVisible: boolean = false;
  formData = {
    name: '',
    description: ''
  };

  @Output() hideOverlay = new EventEmitter<void>();

  onCloseClick(): void {
    this.hideOverlay.emit();
  }

  onFormSubmit() {
    // Logic to handle form submission
    console.log(this.formData);
    // Close the overlay or perform other actions
    this.onCloseClick();
  }
}
<div class="overlay" *ngIf="isVisible" (click)="onCloseClick()">
  <div class="overlay-content" style="background-color: white; margin: auto; text-align: center; align-items: center; border-radius: 10px; ">
    <h2>Overlay Form</h2>
    <form (submit)="onFormSubmit()">
      <label for="name">Name:</label>
      <input type="text" id="name" name="name" [(ngModel)]="formData.name" required>

      <label for="description">Description:</label>
      <textarea id="description" name="description" [(ngModel)]="formData.description" rows="4" required></textarea>

      <div style="padding: 10px;">
        <button class="button" type="submit">Submit</button>
        <button class="button" (click)="onCloseClick()">Close</button>
      </div>
    </form>
  </div>
</div>
