import { Component, OnInit } from '@angular/core';
import { DataService } from './data.service';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})


export class AppComponent implements OnInit {
  notes: any;

  constructor(private dataService: DataService) { }

  ngOnInit():void {
    this.dataService.getData().subscribe(data => {
      this.notes = data; // Assign the fetched data to the component property
      console.log(data);
    });
  }
}


<body style="margin: 0;padding: 0;">



<div class="container" style="display: block;border-width: 0px;margin: 0;padding: 0;background-image: url(../assets/pictures/pexels-abdullah-ghatasheh-1631677\ \(1\).jpg);">

<div class="title" style="font-size: 20px; text-align: center;margin: 0;padding: 0; ">

<h1 style="margin: 0;padding: 0;">Notes</h1>
</div>

<div class="entire-container" style="margin-top: 100px;padding: 50px;padding-bottom: 300px;">
  <ul>

    <ul class="note-container" style="display: flex; flex-wrap:wrap; justify-content:space-evenly;">
      <li *ngFor="let note of notes" style="list-style: none; padding: 10px;">
        <div style="background-color: aqua; font-size: 25px;  padding: 30px; border-radius: 10px;">
          <div class="note-name" style="padding-bottom: 10px; font-weight: bold;">
            {{note.name}}
          </div>
          <div class="note-category" style="padding-bottom: 10px;">
            {{note.category}}
          </div>
          {{note.description}}
        </div>
      </li>
    </ul>
  </ul>

</div>

</div>

</body>
